INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (1, 'Газета', 'Периодическое газетное издание, выходящее через краткие промежутки времени, содержащее официальные материалы, оперативную информацию и статьи по актуальным общественно-политическим, научным, производственным и другим вопросам, а также литературные произведения в рекламу');
INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (2, 'Журнал', 'Периодическое журнальное издание, содержащее статьи или рефераты по различным общественно-политическим, научным, производственным и др. вопросам, литературно-художественные произведения, имеющее постоянную рубрикацию, официально утвержденное в качестве данного вида издания');
INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (3, 'Бюллетень', 'Периодическое или продолжающееся издание, выпускаемое оперативно, содержащее краткие официальные материалы по вопросам, входящим в круг ведения выпускающей его организации');
INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (4, 'Календарь', 'Периодическое справочное издание, содержащее последовательный перечень дней, недель, месяцев данного года, а также другие сведению различного характера');
INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (5, 'Реферативный сборник', 'Периодическое или продолжающееся реферативное издание, выходящее в виде сборника');
INSERT INTO periodicals.periodical_categories (periodical_category_id, periodical_category_name, periodical_category_description) VALUES (6, 'Экспресс-информация', 'Периодическое реферативное издание, содержащее расширенные и сводные рефераты наиболее актуальных зарубежных опубликованных материалов или отечественных неопубликованных документов, требующих оперативного оповещения');